# Define email parameters
$Sender = "techteam@motiotech.com"
$Subject = 'Action Required: Reset Your Active Directory Password'
$smtpserver = "smtp.gmail.com"
$port = 587
$username = "SMTP USER ACCOUNT"
$password = "SMTP USER ACCOUNT PASSWORD"
$currentDate = Get-Date

function SendEmail {
    param (
        [Parameter(Mandatory=$true)]
        [string]$Recipient,
        [Parameter(Mandatory=$true)]
        [string]$Body
    )

    # Create a new email message
    $message = New-Object System.Net.Mail.MailMessage
    $message.From = $Sender
    $message.To.Add($Recipient)
    $message.Subject = $Subject
    $message.Body = $Body
    $message.IsBodyHtml = $true

    # Create SMTP client and configure settings
    $client = New-Object System.Net.Mail.SmtpClient($smtpserver, $port)
    $client.EnableSsl = $true # Enable SSL for secure connection
    $client.Credentials = New-Object System.Net.NetworkCredential($username, $password)

    # Send the email
    try {
        $client.Send($message)
        Write-Host "Email sent successfully to $Recipient"
    }
    catch {
        Write-Host "Failed to send email to $Recipient. Error: $_"
    }

    # Clean up resources
    $message.Dispose()
}

# Get users from Active Directory
$Users = Get-ADUser -SearchBase 'OU=Motio-Users,DC=motio,DC=local' -Filter {Enabled -eq $True -and PasswordNeverExpires -eq $False} -Properties msDS-UserPasswordExpiryTimeComputed, EmailAddress, Name | Select-Object Name, @{Name ="ExpirationDate";Expression= {[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed")}}, EmailAddress

foreach ($user in $Users) {
    $daysToExpire = ($user.ExpirationDate - $currentDate).Days
    if ($daysToExpire -eq 13 -or $daysToExpire -eq 5 -or $daysToExpire -eq 1) {
        $BodyTxt3 = @"
<h3>How to Reset Your Password:</h3>
<ol>
    <li>While logged into your computer, press <strong>Ctrl+Alt+Del</strong> on your keyboard Or If you are working remotely, press <strong>Ctrl+Alt+End</strong></li>
    <li>Choose "Change Password"</li>
    <li>Enter your current password</li>
    <li>Create a new password following our password policy guidelines:</li>
    <ul>
        <li>Must be at least 12 characters long</li>
        <li>Include a mix of uppercase, lowercase, numbers, and symbols</li>
        <li>Cannot be a commonly used password</li>
        <li>Cannot be one of your last 5 passwords</li>
    </ul>
    <li>Confirm and save your new password</li>
</ol>
"@

        $EmailBody = @"
<html>
<body style='font-family: Arial, sans-serif; text-align: center;'>
    <p style='text-align: left;'>Dear $($user.Name),</p>
    <p style='text-align: left;'>For security reasons, we periodically require all employees to reset their Active Directory passwords. Your password is set to expire in <strong>$daysToExpire days</strong>.</p>
    <div style='text-align: left;'>$BodyTxt3</div>
    <p style='text-align: left;'>If you have any issues resetting your password or need assistance, please contact the IT Help Desk at <a href='mailto:support@motiotech.com'>support@motiotech.com</a> or call 616-843-0843.</p>
    <p style='text-align: left;'>Thank you for your cooperation in this important security measure.</p>
    <p style='text-align: left;'>Regards,<br/> Motio<br/> IT Security Team</p>
    <p style='margin:0in;font-size:15px;font-family:"Calibri",sans-serif;'>
        <img width="1145" src="data:image/jpeg;base64," alt="Company Logo">
    </p>
</body>
</html>
"@

        SendEmail -Recipient $user.EmailAddress -Body $EmailBody
    }
}